window.addEventListener("scroll",()=>{let e=document.querySelector('header[data-sticky="header"]'),t=e.offsetTop;window.pageYOffset>t?(e.classList.add("fixme"),document.querySelector(".main").classList.add("header_fixed")):(e.classList.remove("fixme"),document.querySelector(".main").classList.remove("header_fixed"))}),document.querySelector("[data-menu-target]").addEventListener("click",()=>{document.querySelector(".menu-container").classList.add("active")}),document.querySelector("[data-menu-close]").addEventListener("click",()=>{document.querySelector(".menu-container").classList.remove("active")});
